// Generic Configurations
export const AUTH_CACHE_KEY = 'chunky/account'
export const CONTEXT_CACHE_KEY = 'chunky/context'

import { connect } from 'react-redux'

function mapStateToProps (selectors) {
  return (state, props) => {
    if (!props.chunkName || !state[props.chunkName]) {
      return props
    }

    var newProps = {}
    for (let name in selectors) {
      const selector = selectors[name]

      if (name === '@') {
        newProps[name] = Object.assign({}, selector)
        continue
      }

      const selectorProps = (options) => Object.assign(options || {}, { _route: props })
      const result = (options) => selector(state, selectorProps(options))
      newProps[name] = result
    }

    return newProps
  }
}

function mapDispatchToProps (actions) {
  return (dispatch, props) => {
    var newProps = {}
    for (let action in actions) {
      const operation = actions[action].op
      const actionProps = (options) => Object.assign(options || {}, { _route: props })
      newProps[action] = (options) => dispatch(operation(actionProps(options)))

      if (action !== 'startOperation') {
        newProps[`@${action}`] = Object.assign({}, actions[action])
      }
    }

    return newProps
  }
}

export default function (component, selectors, actions) {
  return connect(
    mapStateToProps(selectors),
    mapDispatchToProps(actions)
  )(component)
}

import * as Errors from '../errors'

export default class DataProvider {
  constructor (props) {
    this._props = Object.assign({}, this.defaults, props)
  }

  get defaults () {
    return {}
  }

  get props () {
    return this._props
  }

  operation (options) {
    if (!options || !options.type) {
      // We require a type for each operation
      return Promise.reject(Errors.UNDEFINED_OPERATION())
    }

    // Let's check the type of operation we want to execute
    const type = options.type.toLowerCase()
    var executor = this[`${type.toLowerCase()}`]

    if (!executor) {
      // Looks like we don't support such operation types
      return Promise.reject(Errors.UNDEFINED_OPERATION())
    }

    // Bind first
    executor = executor.bind(this)

    // Resolve the nodes
    const nodes = (options.nodes ? options.nodes.map(node => (node.charAt(0) === ':' ? (options.props[node.substring(1)] || node) : node)) : [])

    options.props && delete options.props._route

    // We should be able to execute it now
    return executor({ nodes, options: options.options || {}, props: options.props || {} })
  }
}

export default class ChunkyError extends Error {
  constructor (message) {
    super()

    this.message = `${message}`
    this.stack = (new Error()).stack
    this.name = 'Chunky'
  }
}

import URL from 'url-parse'
import Container from './Container'
import {
  Actions,
  Selectors,
  Reducers
} from '../data'

export default class Generator {
  constructor (props) {
    this._props = props
  }

  get props () {
    return this._props
  }

  generateSelectors (chunk, route, routeName) {
    const hasData = Selectors.common.hasData(chunk.name, 'main')
    const data = Selectors.common.getData(chunk.name)
    const action = Selectors.common.getAction(chunk.name)
    const hasDataError = Selectors.common.hasError(chunk.name, 'main')
    const dataError = Selectors.common.getError(chunk.name)
    const isDataLoaded = Selectors.common.isDone(chunk.name)
    const isDataLoading = Selectors.common.isInProgress(chunk.name)

    return { '@': { id: `${chunk.name}/${routeName}`, route, routeName }, hasData, data, hasDataError, dataError, isDataLoaded, isDataLoading, action }
  }

  generateAction (chunk, options) {
    if (!options || !options.provider || !this.props.dataProviders[options.provider]) {
      // All actions must specify an operation and a data provider
      return
    }

    // Look up the data provider first
    const provider = this.props.dataProviders[options.provider]

    if (!provider) {
      // We want to make sure we have a valid data provider before we move on
      return
    }

    // Let's build up the operation from the data provider
    const operation = (props) => provider.operation(Object.assign({ props }, options))

    // And finally, let's use that operation to generate an action
    return (props) => Actions.common.asyncAction(`${options.chunkName}/${options.func}`, () => operation(props), Object.assign({ props }, options))
  }

  parseOperationFromURI (uri, chunk) {
    const url = new URL(uri, true)

    const type = url.hostname
    const provider = url.protocol.slice(0, -1).toLowerCase()
    const nodes = url.pathname.split('/').slice(1)
    const options = url.query
    const flavor = url.hash ? url.hash.substring(1) : 'main'
    const chunkName = (provider === 'local' && nodes.length > 0 ? nodes[0] : chunk.name)

    return { type, nodes, options, flavor, provider, chunkName }
  }

  generateActions (chunk, route, routeName) {
    if (!route || !route.operations || Object.keys(route.operations).length === 0) {
      return {}
    }

    var all = {}

    for (const operationName in route.operations) {
      // Parse the action from the URI
      var operationUri = route.operations[operationName]
      var operationHandlers = {}

      if (Array.isArray(operationUri) && operationUri.length > 1) {
        operationHandlers = operationUri[1]
        operationUri = operationUri[0]
      }

      // Here's our operation now, all parsed
      const operation = Object.assign({ func: operationName, handlers: operationHandlers, routeName, routeId: `${chunk.name}/${routeName}` }, this.parseOperationFromURI(operationUri, chunk))

      // Attempt to generate this action
      const generatedAction = this.generateAction(chunk, operation)

      if (generatedAction) {
        // Keep track of it if it was successfully generated
        all[operation.func] = Object.assign({}, { op: generatedAction }, operation, operationHandlers)

        if (Object.keys(all).length === 1) {
          // Let's track this as the initial operation
          all.startOperation = Object.assign({}, { op: generatedAction }, operation, operationHandlers)
        }
      }
    }

    return all
  }

  generateContainer (chunk, route, routeName) {
    const actions = Object.assign({}, this.generateActions(chunk, route, routeName))
    const selectors = Object.assign({}, this.generateSelectors(chunk, route, routeName))

    return Container(route.screen, selectors, actions)
  }

  generateReducer (chunk) {
    return Reducers.common.asyncReducer(chunk.name)
  }
}

import { Component } from 'react'
import URL from 'url-parse'
import { AllHtmlEntities } from 'html-entities'
// import { diff } from 'deep-diff'

export default class Screen extends Component {
  constructor (props) {
    super(props)

    this.state = { lastTransitionTimestamp: '', visible: true, progress: false }
    this._entities = new AllHtmlEntities()
    this._containerId = props['@'] ? props['@'].id : undefined
  }

  get entities () {
    return this._entities
  }

  get containerId () {
    return this._containerId
  }

  get isContainer () {
    return (this.containerId !== undefined)
  }

  componentDidMount () {
    // Automatically attempt to retrieve the main data, if possible and if desired
    if (this.props.startOperationsOnMount && this.props.startOperation) {
      this.props.startOperation()
    }
  }

  updateProgress (progressTitle) {
    this.setState({ progressTitle })
  }

  componentWillMount () {
    for (const transitionName in this.props.transitions) {
      // Inject all transitions into this screen
      this.injectTransition(this.props.transitions[transitionName])
    }
  }

  componentWillUnmount () {
  }

  injectTransition (transition) {
    this.transitions = this.transitions || {}
    this.transitions[transition.name] = (data) => {
      this.transition(transition, data)
    }
  }

  // injectBasicAuth (url) {
  //   if (!this.props.basicAuth) {
  //     return url
  //   }
  //
  //   const urlRef = new URL(url)
  //   const protocol = urlRef.protocol
  //   const link = url.substring(protocol.length + 2)
  //   return `${protocol}//${this.props.basicAuth.username}:${this.props.basicAuth.password}@${link}`
  // }

  transition (transition, data) {
    const timeSinceLastTransition = Date.now() - this.state.lastTransitionTimestamp
    if (this.state.lastTransitionTimestamp && timeSinceLastTransition < 500) {
      // Ignore transition
      return
    }

    // Turn off the progress
    if (this.state.progress) {
      this.setState({ progress: false })
    }

    // Timestamp this transition
    this.setState({ lastTransitionTimestamp: Date.now(), visible: false })
    this[`${transition.type.toLowerCase()}Transition`] && this[`${transition.type.toLowerCase()}Transition`](transition, Object.assign({}, data, { transition }))
  }

  _operationDidFinish (name, data, operation, handler) {
    if (typeof operation[handler] !== 'string') {
      // We only handle simple handlers at the moment
      return
    }

    // Let's see what we have as a handler
    const parts = operation[handler].split(':')

    if (parts && parts.length === 2) {
      // Perform the transition
      const transition = `${parts[1].charAt(0).toUpperCase()}${parts[1].substring(1)}`
      this.transitions[`show${transition}`] && this.transitions[`show${transition}`]({ [operation.flavor]: data })
      return
    }

    if (parts && parts.length === 1) {
      // Execute the specified operation
      this[operation[handler]] ? this[operation[handler]](data) : (this.props[operation[handler]] && this.props[operation[handler]](data))
    }
  }

  isForeignOperation (operation) {
    const foreign = (this.containerId !== operation.routeId)
    return foreign
  }

  operationDidFinish (name, data, error, operation) {
    if (operation && operation.onError && error && error[operation.flavor]) {
      return this._operationDidFinish(name, error[operation.flavor], operation, 'onError')
    }

    if (operation && operation.onSuccess && (!error || !error[operation.flavor])) {
      // The operation response is successful
      return this._operationDidFinish(name, data ? data[operation.flavor] || {} : {}, operation, 'onSuccess')
    }
  }

  shouldComponentUpdate (nextProps, nextState) {
    return true
  }

  componentWillReceiveProps (nextProps) {
    const operation = (nextProps.action ? this.props[`@${nextProps.action()}`] : undefined)

    if (operation && !this.isForeignOperation(operation) && this.props.isDataLoading() && nextProps.isDataLoaded()) {
      // Looks like an operation just finished, so let's trigger the callback
      this.operationDidFinish(nextProps.action(), nextProps.data(), nextProps.dataError(), operation)
    }
  }

  renderDataError ({ main }) {
    throw new Error('Chunky says: implement renderDataError in your route.')
  }

  renderDataLoading () {
    throw new Error('Chunky says: implement renderDataLoading in your route.')
  }

  renderDataDefaults () {
    throw new Error('Chunky says: implement renderDataDefaults in your route.')
  }

  renderData () {
    throw new Error('Chunky says: implement renderData in your route.')
  }

  renderDataChecks () {

  }

  render () {
    if (this.state.progress && this.renderProgress) {
      return this.renderProgress()
    }

    if (this.props.isDataLoading && this.props.isDataLoading() && this.renderDataLoading) {
      // We're loading the data still
      return this.renderDataLoading()
    }

    if (this.props.hasDataError && this.props.hasDataError() && this.renderDataError) {
      // Looks like there's an error that we need to handle
      return this.renderDataError(this.props.dataError())
    }

    if (this.props.hasData && !this.props.hasData() && this.renderDataDefaults) {
      // This screen does not have any data to render
      return this.renderDataDefaults()
    }

    if (this.props.hasData && this.props.hasData() && this.renderData) {
      return this.renderData(this.props.data())
    }
  }
}

export const type = (name, state) => `@@Chunky/${state.toUpperCase()}/${name}`
export const timestamp = () => Date.now()

export const start = (name, options) => ({ 
  type:  type(name, "start"), 
  timestamp: timestamp(), 
  flavor: options.flavor, 
  provider: options.provider 
})

export const error = (name, error, options) => ({ 
  type:  type(name, "error"), 
  flavor: options.flavor, 
  provider: options.provider, 
  error, 
  timestamp: timestamp() 
})

export const ok = (name, data, options) => ({ 
  type:  type(name, "ok"), 
  data, 
  flavor: options.flavor, 
  provider: options.provider, 
  timestamp: timestamp() 
})

export function asyncAction (name, operation, options) {
  if (options.flavor) {
    // Let's resolve the inner flavor, if necessary
    var flavor = options.flavor.split('/')
    if (flavor.length > 0 && options.props[flavor[1]]) {
      options.flavor = [flavor[0], options.props[flavor[1]]].join('/')
    }
  }

  return (dispatch) => {
    dispatch(start(name, options))
    operation().
      then(data => dispatch(ok(name, data, options))).
      catch(err => dispatch(error(name, err, options)))
  }
}

