import * as Errors from '../../errors'
import * as Config from '../../config'
import ChunkyError from '../../core/Error'
import DataProvider from '../../core/DataProvider'

import {
  retrieveAuthToken
} from '../cache'

export default class RestDataProvider extends DataProvider {

  get defaults () {
    return {
      // Defaults
      timeout: 10000,
      headers: {
        'Content-Type': Config.API_JSON_CONTENT_TYPE,
        'Accept': Config.API_JSON_CONTENT_TYPE,
        'Cache-Control': Config.API_DEFAULT_CACHE
      },
      url: Config.API_DEFAULT_SERVER_URL
    }
  }

  create ({ nodes, options, props }) {
     // Let's see what kind of a resource we want to create
    const resource = nodes[0]

    if (!resource) {
      // We require a resource to be defined
      return Promise.reject(Errors.UNDEFINED_OPERATION())
    }

    // Construct the endpoint
    const endpoint = nodes.join('/')

    const request = {
      method: 'post',
      timeout: this.props.timeout,
      url: `${this.props.url}/${endpoint}`,
      headers: this.props.headers,
      body: props
    }

    return this._sendRequest(request)
  }

  retrieve ({ nodes, options, props }) {
     // Let's see what kind of a resource we want to retrieve
    const resource = nodes[0]

    if (!resource) {
      // We require a resource to be defined
      return Promise.reject(Errors.UNDEFINED_OPERATION())
    }

    // Construct the endpoint
    const endpoint = nodes.join('/')

    const request = {
      method: 'get',
      timeout: this.props.timeout,
      url: `${this.props.url}/${endpoint}`,
      headers: this.props.headers
    }

    return this._sendRequest(request)
  }

  _prepareRequest ({ url, method, headers, body }) {
    // Prepare the request properties
    const options = {
      method: method.toUpperCase(),
      headers
    }

    if (body) {
      // Inject the body if found
      options.body = JSON.stringify(body)
    }

    return { url, options }
  }

  _parseResponse (response) {
    return new Promise((resolve, reject) => {
      if (!response || response === undefined || Object.keys(response).length === 0) {
          // If the response does not contain a json payload, we won't fail this
          // response but we'll send it back with a warning
        resolve({ status: 0, warning: Errors.WARNING_EMPTY_RESPONSE, data: {} })
        return
      }

        // We do have some json, so let's try to parse it
      response.json()

            // Looks like the json is valid, the request is good to go now
            .then(json => resolve({ status: response.status, data: json }))

            // Sounds like an invalid json; we don't fail the response but we
            // will need to flag it as a warning
            .catch(err => resolve({ status: response.status, warning: Errors.WARNING_INVALID_RESPONSE, data: {} }))
    })
  }

  _timeout (ms, promise) {
    return new Promise(function (resolve, reject) {
      setTimeout(function () {
        reject(Errors.TIMEOUT_ERROR())
      }, ms)
      promise.then(resolve, reject)
    })
  }

  _sendRequest (request) {
    const requestParams = this._prepareRequest(request)
    return this._timeout(request.timeout, fetch(requestParams.url, requestParams.options))
            .then((response) => this._parseResponse(response))
            .then((response) => response.data)
  }

}

export const asyncReducer = (name) => {
  return (state = {}, action) => {
    if (!action || Object.keys(action).length === 0 || !action.type) {
      // We don't tolerate empty actions
      return state
    }

    // Let's see what kind of action this is
    const [source, actionState, chunkName, actionName] = action.type.split('/')

    if (source.toLowerCase() != '@@chunky') {
      // We only recognize framework actions
      return state
    }

    if (!chunkName || (name.toLowerCase() != chunkName.toLowerCase())) {
      // We want to ignore foreign actions
      return state
    }

    // Figure out the flavor
    const flavor = (!action.flavor ? ['main'] : (action.flavor.split('/') || ['main']))
    const newErrorFlavor = (flavor.length > 1 ? { [flavor[1]]: action.error } : action.error)
    const newDataFlavor = (flavor.length > 1 ? { [flavor[1]]: action.data } : action.data)

    // The action timestamp
    const timestamp = action.timestamp

    // The data provider
    const provider = action.provider

    // Let's work on the new state
    var newState = { flavor: action.flavor, timestamp, provider, inProgress: false, done: true, action: actionName }
    var data = Object.assign({}, state.data)
    var error = Object.assign({}, state.error)

    switch (actionState.toLowerCase()) {
      case 'start':
        return Object.assign({}, newState, { inProgress: true, done: false, data, error })

      case 'error':
        if (newErrorFlavor) {
          error[flavor[0]] = newErrorFlavor
        }
        return Object.assign({}, newState, Object.keys(error).length > 0 ? { error } : {})

      case 'ok':
        if (newDataFlavor) {
          data[flavor[0]] = newDataFlavor
        }
        return Object.assign({}, newState, Object.keys(data).length > 0 ? { data } : {})

      default:
    }

    return state
  }
}

